# Create a MongoDB container with custom port, bind mount for persistent data, and root user credentials
docker container create --name mongodata --publish 27018:27017 --mount "type=bind,source=/Users/zadid/Documents/Developments/TUTORIAL/DOCKER/belajar-docker-dasar/mongo-data,destination=/data/db" --env MONGO_INITDB_ROOT_USERNAME=zadid --env MONGO_INITDB_ROOT_PASSWORD=zadid mongo:latest